import BookingCard from "@/components/host/bookings/BookingCard";
import SearchBarWithoutModal from "@/components/host/SearchBarWithoutModal";
import React, { useState } from "react";
import {
  Keyboard,
  SafeAreaView,
  ScrollView,
  Text,
  TouchableWithoutFeedback,
} from "react-native";

export default function HostBookingsPage() {
  const [query, setQuery] = useState("");
  // Mock data for bookings
  const mockBookings = [
    {
      id: "1",
      imageUri:
        "data:image/jpeg;base64,", // Replace with a valid user image URI
      userName: "Guran Fada",
      checkInDate: "April 27, 2025",
      checkOutDate: "April 30, 2025",
      initialStatus: "Pending",
    },
    {
      id: "2",
      imageUri:
        "data:image/jpeg;base64,", // Replace with another valid user image URI
      userName: "Jane Doe",
      checkInDate: "May 10, 2025",
      checkOutDate: "May 15, 2025",
      initialStatus: "Approved",
    },
    {
      id: "3",
      imageUri:
        "data:image/jpeg;base64,", // Replace with another valid user image URI
      userName: "John Smith",
      checkInDate: "June 1, 2025",
      checkOutDate: "June 7, 2025",
      initialStatus: "Rejected",
    },
  ];

  const [openDropdownId, setOpenDropdownId] = useState<string | null>(null);

  const handleToggleDropdown = (bookingId: string) => {
    setOpenDropdownId(openDropdownId === bookingId ? null : bookingId);
  };

  const handleStatusChange = (
    bookingId: string,
    status: "Pending" | "Approved" | "Rejected"
  ) => {
    console.log(`Booking ${bookingId} status changed to ${status}`);
    // Here you would typically update the actual booking data state or send an API request
    // For mock data, we'll just log the change and close the dropdown.
    setOpenDropdownId(null); // Close dropdown after status change
  };

  return (
    <TouchableWithoutFeedback onPress={Keyboard.dismiss}>
      <SafeAreaView className="flex-1 relative mx-4 gap-4">
        <Text className="text-3xl font-bold mt-4">Bookings</Text>
        <SearchBarWithoutModal
          query={query}
          setQuery={setQuery}
          placeholder="Search bookings..."
        />
        <ScrollView className="flex-1 ">
          {mockBookings.map((booking) => (
            <BookingCard
              key={booking.id}
              imageUri={booking.imageUri}
              userName={booking.userName}
              checkInDate={booking.checkInDate}
              checkOutDate={booking.checkOutDate}
              initialStatus={
                booking.initialStatus as "Pending" | "Approved" | "Rejected"
              }
              onStatusChange={(status) =>
                handleStatusChange(booking.id, status)
              }
              isDropdownOpen={openDropdownId === booking.id}
              onToggleDropdown={() => handleToggleDropdown(booking.id)}
            />
          ))}
        </ScrollView>
      </SafeAreaView>
    </TouchableWithoutFeedback>
  );
}
